var answer_list;
var isStarted = false;
var needStop = false;

function getStart() {
	
	isStarted = true;

	$('#button_start').html('停止');

	//获取`display != none`的元素, 取第一个
	var targetLi = $("ul.W_ti_ul li:visible");
	if(targetLi.length > 0) {

		var firstLi = targetLi.eq(0);		
		var answerStrs = new Array();
 	 		 
		//模拟答题
		if (/monidati.html/.test(window.location.href)) { //表示是模拟答题
			
			//answerStrs =  firstLi.find('div.W_ml45').attr('answer'); // A,B,C
			
			var question = $(firstLi[0]).find('h1 span.w_fz18').eq(1).text();			
			answerStrs = searchNewAnswer(question, firstLi[0]);

		}else  if (/kaishijingsai.html/.test(window.location.href)) { //正式答题
			
			//非模拟答题的情况 baseURI
			console.log(targetLi);
			console.log(firstLi[0]);
			var question = $(firstLi[0]).find('h1 span.W_ml10').eq(0).text();		
			console.log(question);
			
			//测试
			answerStrs = searchNewAnswer(question, firstLi[0]);
			
		}else {
			return;
		}

		var map = {
			'A': 0,
			'B': 1,
			'C': 2,
			'D': 3
		};

		if(answerStrs.length == 1) {
			//单选
			var index = map[answerStrs[0]];
			firstLi.find('input[type=radio]').eq(index).click();

		} else if(answerStrs.length > 1) {
			//多选
			for(var i = 0; i < answerStrs.length; i++) {
				var chars = answerStrs[i];
				var index = map[chars];
				firstLi.find('input[type=checkbox]').eq(index).click();
			}

			//单选
			var index = map[answerStrs[0]];
			firstLi.find('input[type=radio]').eq(index).click();
		}


		//递归结束条件
		if($('.w_btn_tab_down').hasClass('W_bgcol') && answerStrs.length != 0 ) {
			
			stopAnswering();
			return;
			
		} else {

			if (needStop) {
				stopAnswering();
				return;
			};

			setTimeout(function() {
					$('.w_btn_tab_down').click();
					getStart();
				},
			2000);
		}
		
	}
}

function stopAnswering(){
	isStarted = false ;
	needStop = false ;
	$('#button_start').html('开始答题');
}

function searchNewAnswer(question,selections){

	//测试
	//var res = tikuString.match(/(?<=\n全党要坚定执行党的政治路线，严格遵守政治纪律和政治规矩，在政治立场、（）上同党中央保持高度一致。)[\s\S]*?(?=\*{5})/g); //返回数组	
	//var regExp = new RegExp( "(?<=\n" + question + ")[\s\S]*?(?=\*{5})","g");
	//(?<=\n问题描述)[\s\S]*?(?=\*{5})/g); 	
	//需要转义,否则报错:	Invalid regular expression: missing /
	
	console.log('问题:'+question);
	//console.log(selections);

	var question_split=question.split('（）');
	
	var current_question='';
	for (var i = 0; i < answer_list.length; i++) {
		var item=answer_list[i];
		if(item.indexOf(question_split[0])>-1&&item.indexOf(question_split[1])>-1){	
			current_question=item;			
			break;
		}
	}
	if(current_question==''){
		return new Array();
	}


	var answerString=current_question.replace(question_split[0],'');
	answerString=answerString.replace(question_split[1],'');

	console.log('答案：'+answerString)

	var selectionDivs = $(selections).find('div.W_ml45');
	var answerLetter=new Array();
	for (var i = 0; i < selectionDivs.length; i++) {
	 		
	 	var node = selectionDivs.eq(i);	 		 			
		var textString = node.find('sapn').text(); //A.政治制度						
		var answer = textString.split('.');						
		if(answerString.indexOf(answer[1])>-1)	{
			answerLetter.push(answer[0]);
		}
	 }	 		 	


	return answerLetter;	

}



function processQuestionLibrary() {

    var tiku_list=[tiku_12,tiku_1,tiku_2,tiku_3,tiku_4,tiku_5];

    var answer_list = [];

    for(var i=0;i<tiku_list.length;i++){

    	var questionList = tiku_list[i]['data']['subjectInfoList'];
    	getAnswerList(questionList,answer_list);
    }
        

    return answer_list;
}

function getAnswerList(question_list,answer_list) {

	for (var i = 0; i < question_list.length; i++) {
        var question = question_list[i];       
        var options = question['optionInfoList'];
        var answer = [];
        for (var j = 0; j < options.length; j++) {
            var option = options[j];
            if (option['isRight'] == "1") {
                answer.push(option['optionTitle'])
            }
        }
        var answerString = answer.join(',');
        var origin_question = question['subjectTitle'];
        var answer_data = origin_question.replace('（）', answerString);        
        answer_list.push(answer_data);
    }
}

